pragma solidity ^0.4.2;

import "zeppelin-solidity/contracts/token/ERC20/StandardToken.sol";

contract SuperToken is StandardToken{

    string public name;                   //fancy name: eg Simon Bucks
    uint8 public decimals;                //How many decimals to show. ie. There could 1000 base units with 3 decimals. Meaning 0.980 SBX = 980 base units. It's like comparing 1 wei to 1 ether.
    string public symbol;                 //An identifier: eg SBX
    string public version = 'H1.0';       //human 0.1 standard. Just an arbitrary versioning scheme.

    // function SuperToken() public{
    //     balances[msg.sender] = 1*10**28;               // Give the creator all initial tokens 
    //     totalSupply = 1*10**28;   // Update total supply 
    //     name = "SuperToken2";        // Set the name for display purposes
    //     decimals = 18;           // Amount of decimals for display purposes
    //     symbol = "ST";         // Set the symbol for display purposes
    // }

    function SuperToken(uint256 _initialAmount, string _tokenName, uint8 _decimalUnits, string _tokenSymbol) {
        balances[msg.sender] = _initialAmount; // 初始token数量给予消息发送者
        // totalSupply = _initialAmount;         // 设置初始总量
        name = _tokenName;                   // token名称
        decimals = _decimalUnits;           // 小数位数
        symbol = _tokenSymbol;             // token简称
    }

}
